const ProductDetailScreen = () => {
  const [quantity, setQuantity] = useState(1);

  const { addToCart } = useCart(); // FunÃ§Ã£o para adicionar ao carrinho

  // FunÃ§Ã£o para converter o preÃ§o de string para nÃºmero
  const extractPrice = (priceText: string): number => {
    return parseFloat(priceText.replace("R$", "").replace(",", "."));
  };

  const incrementQuantity = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };

  function handleNavigateCart() {
    router.push("/pages/orders/cart");
  }

  const { id } = useLocalSearchParams(); // Acessar parÃ¢metros usando 'useSearchParams'

  const product = products.find((p) => p.id === id);

  if (!product) {
    return <Text>Produto nÃ£o encontrado</Text>;
  }

  // Extrai o preÃ§o do produto e calcula o total
  const price = extractPrice(product.price || "0");
  const total = price * quantity;

  const handleAddToCartAndNavigate = () => {
    // Verifica se o produto estÃ¡ definido
    if (!product) {
      console.error("Produto nÃ£o encontrado");
      return;
    }

    // Cria o objeto cartItem
    const cartItem = {
      product: {
        id: product.id, // ID do produto
        name: product.name, // Nome do produto
        image: product.image, // Imagem do produto
        price: product.price, // PreÃ§o do produto
      },
      quantity: quantity, // Quantidade selecionada
      totalPrice: total, // PreÃ§o total (preÃ§o * quantidade)
    };

    // Adiciona o item ao carrinho usando a funÃ§Ã£o do contexto
    addToCart(product, quantity, total);

    // Navega para a pÃ¡gina do carrinho
    router.push("/pages/orders/cart");
  };
  return (
    <View style={styles.container}>
      <Image
        source={{ uri: product.image }}
        style={{
          width: 200,
          height: 200,
          borderRadius: 100,
        }}
      />
      <View style={styles.containerproduto}>
        <Text style={styles.textoproduto}>{product.name}</Text>
        <Text className="text-xl font-bold mt-5 mb-3 text-gray-900">
          R$ {total.toFixed(2)}
        </Text>
        <View style={styles.quantityContainer}>
          <TouchableOpacity
            onPress={decrementQuantity}
            style={styles.buttonquantity}
          >
            <MaterialIcons name="remove" size={24} color="#fff" />
          </TouchableOpacity>

          <Text style={styles.quantity}>{quantity}</Text>

          <TouchableOpacity
            onPress={incrementQuantity}
            style={styles.buttonquantity}
          >
            <MaterialIcons name="add" size={24} color="#fff" />
          </TouchableOpacity>
        </View>

        <TouchableOpacity
          style={styles.button}
          onPress={() => {
            handleAddToCartAndNavigate(); // Navega para a pÃ¡gina do carrinho
          }}
        >
          <Text
            style={{
              color: "white",
              fontSize: 16,
              fontWeight: "bold",
              marginRight: 10, // EspaÃ§o entre o texto e o Ã­cone
            }}
          >
            Add no carrinho
          </Text>
          <Ionicons name="cart-outline" size={24} color="white" />
        </TouchableOpacity>
      </View>
    </View>
  );
};

